// Parse all image URLs from the article HTML and prepare for processing
const input = $input.first().json;
const article = input.output || input;
const body = article.body || '';

// Extract all image URLs from HTML (handle both src and SRC)
const imageUrlRegex = /<img[^>]+(?:src|SRC)=["']([^"']+)["'][^>]*>/gi;
const imageUrls = [];
let match;

while ((match = imageUrlRegex.exec(body)) !== null) {
  const url = match[1];
  // Only process external URLs (not already hosted on our server)
  if (url && url.startsWith('http') && !url.includes('mypen.ge')) {
    imageUrls.push({
      originalUrl: url,
      // Extract filename for matching
      filename: url.split('/').pop()?.split('?')[0] || 'image'
    });
  }
}

console.log(`Parsed ${imageUrls.length} images from article`);
imageUrls.forEach((img, idx) => {
  console.log(`  Image ${idx + 1}: ${img.originalUrl.substring(0, 60)}...`);
});

// Store article data for later use
const articleData = {
  ...article,
  imageUrls: imageUrls,
  imageCount: imageUrls.length
};

// If no images, just pass through article
if (imageUrls.length === 0) {
  return [{
    json: {
      output: article,
      skipImageProcessing: true
    }
  }];
}

// Output ONLY image items (not article data) - article data will be retrieved later
// Each image item includes articleData for reference
const result = imageUrls.map(img => ({
  json: {
    originalUrl: img.originalUrl,
    filename: img.filename,
    articleData: articleData  // Store article data with each image for later retrieval
  }
}));

return result;

// Validate and prepare image URL for download
const input = $input.first().json;
const originalUrl = input.originalUrl || '';

console.log('Validate Image URL - Received data:', JSON.stringify(input));
console.log('Validate Image URL - originalUrl:', originalUrl);

if (!originalUrl || typeof originalUrl !== 'string' || !originalUrl.startsWith('http')) {
  throw new Error(`Invalid originalUrl. Received: ${JSON.stringify(input)}`);
}

const filename = input.filename || originalUrl.split('/').pop()?.split('?')[0] || 'image.jpg';

return [{
  json: {
    originalUrl: originalUrl,
    filename: filename,
    articleData: input.articleData || {}
  }
}];

// Map upload response to include originalUrl and hostedUrl
const input = $input.first().json;
const uploadResponse = input;

// The upload endpoint returns: { success, originalUrl, hostedUrl, fullUrl }
const originalUrl = uploadResponse.originalUrl || '';
const hostedUrl = uploadResponse.hostedUrl || '';

if (!hostedUrl) {
  console.error('No hostedUrl in upload response:', uploadResponse);
  throw new Error('Failed to upload image - no hostedUrl returned');
}

console.log(`Image uploaded successfully: ${originalUrl.substring(0, 50)}... -> ${hostedUrl}`);

return [{
  json: {
    originalUrl: originalUrl,
    hostedUrl: hostedUrl,
    fullUrl: uploadResponse.fullUrl || hostedUrl,
    filename: originalUrl.split('/').pop()?.split('?')[0] || 'image'
  }
}];

// Replace all original image URLs with hosted URLs in HTML
// Get article data from the first aggregated item (stored in articleData)
const allItems = $input.all();

// Get article data from any item (they all have articleData)
const articleData = allItems[0]?.json.articleData || {};
let body = articleData.body || '';

if (!body) {
  console.error('No article body found in aggregated items');
  throw new Error('Article body not found');
}

// Get all image replacements from aggregated upload responses
const replacements = [];
allItems.forEach(item => {
  const json = item.json;
  if (json.originalUrl && json.hostedUrl) {
    replacements.push({
      originalUrl: json.originalUrl,
      hostedUrl: json.hostedUrl
    });
  }
});

console.log(`Replacing ${replacements.length} image URLs in HTML`);

// If no replacements, pass through original article
if (replacements.length === 0) {
  return [{
    json: {
      output: articleData
    }
  }];
}

// Replace each URL in the HTML body
replacements.forEach(({ originalUrl, hostedUrl }) => {
  const escapedUrl = originalUrl.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');

  // Replace in img tags with src (lowercase)
  body = body.replace(
    new RegExp(`(<img[^>]+src=["'])${escapedUrl}(["'][^>]*>)`, 'gi'),
    `$1${hostedUrl}$2`
  );

  // Replace in img tags with SRC (uppercase)
  body = body.replace(
    new RegExp(`(<img[^>]+SRC=["'])${escapedUrl}(["'][^>]*>)`, 'gi'),
    `$1${hostedUrl}$2`
  );

  // Also replace standalone URLs
  body = body.replace(new RegExp(escapedUrl, 'g'), hostedUrl);

  console.log(`Replaced: ${originalUrl.substring(0, 50)}... -> ${hostedUrl}`);
});

// Update article with replaced URLs
const updatedArticle = {
  ...articleData,
  body: body
};

return [{
  json: {
    output: updatedArticle
  }
}];

// Prepare webhook payload with proper data mapping
const input = $input.first().json;

// Information Extractor outputs data nested under 'output' object
// Structure: { output: { title: '...', summary: '...', body: '...', featured_image: '...' } }
const article = input.output || input;

// Extract fields from nested output structure
const title = article.title || '';
const summary = article.summary || '';
let body = article.body || '';
const featured_image = article.featured_image || null;

// Validate required fields
if (!title || typeof title !== 'string' || title.trim() === '') {
  throw new Error('Title is required but was empty or missing');
}

if (!body || typeof body !== 'string' || body.trim() === '') {
  throw new Error('Body/content is required but was empty or missing');
}

// Normalize image tags: convert uppercase SRC to lowercase src for consistency
// This ensures the webhook regex can properly match all image tags
body = body.replace(/<img\s+([^>]*?)SRC=/gi, '<img $1src=');
body = body.replace(/<img\s+([^>]*?)src=/gi, '<img $1src=');

// Debug: Log images found in body before sending to webhook
const imgMatches = body.match(/<img[^>]+src=["']([^"']+)["'][^>]*>/gi);
console.log('Images in body before webhook:', imgMatches?.length || 0);
if (imgMatches && imgMatches.length > 0) {
  imgMatches.forEach((img, idx) => {
    const urlMatch = img.match(/src=["']([^"']+)["']/i);
    const url = urlMatch ? urlMatch[1] : 'N/A';
    console.log(`  Image ${idx + 1}: ${url.substring(0, 80)}...`);
  });
}

// Extract first image from body HTML as fallback for featured_image
// The webhook will handle downloading and hosting all images in the content
let firstImageFromBody = null;
if (body) {
  const imgMatch = body.match(/<img[^>]+src=["']([^"']+)["'][^>]*>/i);
  if (imgMatch && imgMatch[1]) {
    firstImageFromBody = imgMatch[1];
  }
}

// Use LLM-provided featured_image or fallback to first image from body
// Handle case where LLM might return 'null' as string
let finalFeaturedImage = featured_image;
if (!finalFeaturedImage || finalFeaturedImage === 'null' || finalFeaturedImage === 'None' || finalFeaturedImage === '') {
  finalFeaturedImage = firstImageFromBody;
}

// Validate featured_image URL format if provided
// Note: Webhook will handle uploading external images
if (finalFeaturedImage && typeof finalFeaturedImage === 'string') {
  try {
    new URL(finalFeaturedImage);
  } catch (e) {
    // Invalid URL, set to null
    finalFeaturedImage = null;
  }
}

// Prepare payload matching webhook schema
// Note: Webhook will automatically download and replace external image URLs in content
const payload = {
  title: title.trim(),
  title_ka: title.trim(),
  content: body.trim(),
  content_ka: body.trim(),
  excerpt: (summary || '').trim(),
  excerpt_ka: (summary || '').trim(),
  featured_image: finalFeaturedImage || null,
  author: 'Mypen Team',
  published: true
};

// Final validation
if (payload.title.length === 0) {
  throw new Error('Title cannot be empty after trimming');
}

if (payload.content.length === 0) {
  throw new Error('Content cannot be empty after trimming');
}

console.log('Payload prepared - content length:', payload.content.length);
console.log('Payload prepared - images will be processed by webhook');

return [{ json: payload }];

