// Prepare webhook payload with proper data mapping
const input = $input.first().json;

// Information Extractor outputs data nested under 'output' object
// Structure: { output: { title: '...', summary: '...', body: '...', featured_image: '...' } }
const article = input.output || input;

// Extract fields from nested output structure
const title = article.title || '';
const summary = article.summary || '';
let body = article.body || '';
const featured_image = article.featured_image || null;

// Validate required fields
if (!title || typeof title !== 'string' || title.trim() === '') {
  throw new Error('Title is required but was empty or missing');
}

if (!body || typeof body !== 'string' || body.trim() === '') {
  throw new Error('Body/content is required but was empty or missing');
}

// Normalize image tags: convert uppercase SRC to lowercase src for consistency
// This ensures the webhook regex can properly match all image tags
body = body.replace(/<img\s+([^>]*?)SRC=/gi, '<img $1src=');
body = body.replace(/<img\s+([^>]*?)src=/gi, '<img $1src=');

// Debug: Log images found in body before sending to webhook
const imgMatches = body.match(/<img[^>]+src=["']([^"']+)["'][^>]*>/gi);
console.log('Images in body before webhook:', imgMatches?.length || 0);
if (imgMatches && imgMatches.length > 0) {
  imgMatches.forEach((img, idx) => {
    const urlMatch = img.match(/src=["']([^"']+)["']/i);
    const url = urlMatch ? urlMatch[1] : 'N/A';
    console.log(`  Image ${idx + 1}: ${url.substring(0, 80)}...`);
  });
}

// Extract first image from body HTML as fallback for featured_image
// The webhook will handle downloading and hosting all images in the content
let firstImageFromBody = null;
if (body) {
  const imgMatch = body.match(/<img[^>]+src=["']([^"']+)["'][^>]*>/i);
  if (imgMatch && imgMatch[1]) {
    firstImageFromBody = imgMatch[1];
  }
}

// Use LLM-provided featured_image or fallback to first image from body
// Handle case where LLM might return 'null' as string
let finalFeaturedImage = featured_image;
if (!finalFeaturedImage || finalFeaturedImage === 'null' || finalFeaturedImage === 'None' || finalFeaturedImage === '') {
  finalFeaturedImage = firstImageFromBody;
}

// Validate featured_image URL format if provided
// Note: Webhook will handle uploading external images
if (finalFeaturedImage && typeof finalFeaturedImage === 'string') {
  try {
    new URL(finalFeaturedImage);
  } catch (e) {
    // Invalid URL, set to null
    finalFeaturedImage = null;
  }
}

// Prepare payload matching webhook schema
// Note: Webhook will automatically download and replace external image URLs in content
const payload = {
  title: title.trim(),
  title_ka: title.trim(),
  content: body.trim(),
  content_ka: body.trim(),
  excerpt: (summary || '').trim(),
  excerpt_ka: (summary || '').trim(),
  featured_image: finalFeaturedImage || null,
  author: 'Mypen Team',
  published: true
};

// Final validation
if (payload.title.length === 0) {
  throw new Error('Title cannot be empty after trimming');
}

if (payload.content.length === 0) {
  throw new Error('Content cannot be empty after trimming');
}

console.log('Payload prepared - content length:', payload.content.length);
console.log('Payload prepared - images will be processed by webhook');

return [{ json: payload }];

