// Combine content from multiple sources with fallback logic
// Priority: HTTP Get > Firecrawl > Google Sheet

const allItems = $input.all();
const loopItem = $('Loop Over Items').item.json;

// Try to get content from HTTP Get (first item if successful)
let content = '';
let contentSource = 'unknown';

// Check HTTP Get response (should be first item)
const httpItem = allItems.find(item => item.json.data || item.json.body);
if (httpItem && httpItem.json.data) {
  content = httpItem.json.data;
  contentSource = 'http_get';
  console.log('Using content from HTTP Get');
} else if (httpItem && httpItem.json.body) {
  content = httpItem.json.body;
  contentSource = 'http_get';
  console.log('Using content from HTTP Get (body)');
}

// Fallback to Firecrawl if HTTP Get failed
if (!content || content.trim().length < 100) {
  const firecrawlItem = allItems.find(item => item.json.markdown || item.json.html);
  if (firecrawlItem) {
    content = firecrawlItem.json.html || firecrawlItem.json.markdown || '';
    contentSource = 'firecrawl';
    console.log('Falling back to Firecrawl content');
  }
}

// Final fallback to Google Sheet content
if (!content || content.trim().length < 100) {
  content = loopItem.content || loopItem.contentencoded || loopItem.contentSnippet || '';
  contentSource = 'google_sheet';
  console.log('Falling back to Google Sheet content');
}

// Validate we have content
if (!content || content.trim().length === 0) {
  throw new Error('No content available from any source');
}

console.log(`Content source: ${contentSource}, length: ${content.length} chars`);

return [{
  json: {
    title: loopItem.title || '',
    link: loopItem.link || '',
    content: content,
    contentSource: contentSource,
    originalSheetData: loopItem
  }
}];
